<?phpdefine('IN_PHPBB', true);$phpbb_root_path = '../';$phpEx = substr(strrchr(__FILE__, '.'), 1);include($phpbb_root_path . 'common.' . $phpEx);// PRSinclude($phpbb_root_path . 'includes/functions_prs.' . $phpEx);$user->session_begin();$auth->acl($user->data);$user->setup();page_header('Rate Posts on RolePlayGateway');$threshold = time() - ( 2629743 * 6 );$sql = "SELECT p.post_id FROM gateway_posts p			INNER JOIN gateway_post_stats s ON s.post_id = p.post_id			INNER JOIN gateway_topics t ON p.topic_id = t.topic_id		WHERE p.post_time > $threshold			AND ((s.word_count > 1000) OR (s.votes > 0))			AND t.topic_status = 0			AND t.forum_id NOT IN (135,145,159,160,164,174)";						$sql = "SELECT p.post_id,p.topic_id FROM gateway_posts p			INNER JOIN gateway_post_stats s ON s.post_id = p.post_id			INNER JOIN gateway_topics t ON p.topic_id = t.topic_id		WHERE p.post_time > $threshold			AND ((s.word_count > 500) OR (s.prs_rating > 30))			AND t.topic_status = 0			AND t.forum_id IN (4,102,103,154,111,112,113,167,168,169,170,67,143,73,158)";			$result = $db->sql_query($sql,3600);while ($row = $db->sql_fetchrow($result)) {		$posts[$row['post_id']] = $row['post_id'];}$db->sql_freeresult($result);$sql = "SELECT p.post_id,p.topic_id,p.post_text,p.post_subject,bbcode_uid,bbcode_bitfield,enable_bbcode, enable_smilies, enable_magic_url,topic_id,forum_id FROM gateway_post_stats s			INNER JOIN gateway_posts p ON s.post_id = p.post_id			WHERE s.post_id = ".array_rand($posts)."			ORDER BY s.prs_rating DESC			LIMIT 1";$result = $db->sql_query($sql);while($row = $db->sql_fetchrow($result)) {	$user->setup('mods/prs');		$row['bbcode_options'] = (($row['enable_bbcode']) ? OPTION_FLAG_BBCODE : 0) +    (($row['enable_smilies']) ? OPTION_FLAG_SMILIES : 0) +     (($row['enable_magic_url']) ? OPTION_FLAG_LINKS : 0);		$tags = get_tag_list($row['topic_id'], 10);		$template->assign_vars(array(		'POST_ID'			=> $row['post_id'],		'POST_SUBJECT'		=> $row['post_subject'],		'TOPIC_TAG_LIST'	=> "Tagged with: ".$tags.":",		'POST_TEXT'			=> generate_text_for_display($row['post_text'], $row['bbcode_uid'], $row['bbcode_bitfield'], $row['bbcode_options']),	));}$db->sql_freeresult($result);$template->set_filenames(array(	'body' => 'rating_posts_body.html',));page_footer();?>